      out.write("			}, { threshold: 0.5 }); // Trigger when 50% of the element is in view\r\n");
      out.write("\r\n");
      out.write("			gridCardHeaders.forEach(header => {\r\n");
      out.write("				observer.observe(header);\r\n");
      out.write("			});\r\n");
