      out.write("\r\n");
      out.write("            <h2>Non-Refundable Cases</h2>\r\n");
      out.write("            <p>Refunds will not be provided in the following cases:</p>\r\n");
      out.write("            <ul>\r\n");
      out.write("                <li><strong>Patient Cancellation:</strong> Cancellations made less than 24 hours before the appointment are not eligible for a refund.</li>\r\n");
